import React, { useState, useEffect} from 'react';
import Formulario from './components/Formulario'
import ListodoImagen from './components/ListadoImagen'


function App() {

  const[search, setSearch] = useState("")
  const[images, setImages] = useState([])

  useEffect(()=>{
    const consultaApi = async () =>{
      if(search === '') return

      const imagenesPorPagina = 30;
      const key = '17968933-8b9fd410c145a9f70c1b97c9f'
      const url = `https://pixabay.com/api/?key=${key}&q=${search}&per_page=${imagenesPorPagina}`

      const respuesta = await fetch(url)
      const resultado = await respuesta.json()
      setImages(resultado.hits)
    }
    consultaApi()
  },[search])

  return (


    <div className="container">
      <div className="jumbotron">
        <p className="lead text-center ">Search Image</p>
         <Formulario
          setSearch={setSearch}
         />
         <ListodoImagen
          images = {images}
         />
      </div>
    </div>


  );
}

export default App;
